mapper.sql:
WHERE 1=1

	AND DATE_FORMAT(regDate, '%Y-%m-%d') >= #{schDtStart}


	AND DATE_FORMAT(regDate, '%Y-%m-%d') <= #{schDtEnd}


	AND DATE_FORMAT(startDate, '%Y-%m-%d') >= #{schDtStart}


	AND DATE_FORMAT(endDate, '%Y-%m-%d') <= #{schDtEnd}


	AND empName LIKE CONCAT('%', #{empName}, '%')


	AND specialType LIKE CONCAT('%', #{specialType}, '%')
